mapper.select:
SELECT COUNT(did)
FROM designer
WHERE did=#{did}

SELECT COUNT(demail)
FROM designer
WHERE demail=#{demail}

mapper.update:
update designer
set
introduction = #{introduction}
where did = #{did}

update designer
set
dfilename = #{dfilename}
where did = #{did}

mapper.select:
SELECT dpw
FROM designer
WHERE did=#{did} and demail=#{demail}

SELECT did
FROM designer
WHERE dname=#{dname} and demail=#{demail}

mapper.update:
UPDATE designer
SET validation=1
WHERE did = #{did}

UPDATE certification
SET licenseno = #{licenseno},
licensedate = #{licensedate},
uniquecode1 = #{uniquecode1}
WHERE did = #{did}

UPDATE certification
SET uniquecode2 = #{uniquecode2}
WHERE did = #{did}
