mapper.select:
SELECT *
FROM heart
WHERE uid=#{uid} and did=#{did}

mapper.update:
update heart
set heart_chk = 1
where did=#{did} and uid=#{uid}

update designer
set likecnt = likecnt+1
where did=#{did}

update heart
set heart_chk = 0
where did=#{did} and uid=#{uid}

update designer
set likecnt = likecnt-1
where did=#{did}

mapper.select:
select likecnt
from designer
where did=#{did}

select heart_chk
from heart
where uid = #{uid} and did = #{did}

select count(*)
from heart
where uid = #{uid} and did = #{did}
