mapper.update:
UPDATE user 
SET uphone = #{uphone}
WHERE uid = #{uid}

mapper.select:
SELECT upw
FROM user
WHERE uid = #{uid} and uemail = #{uemail}

SELECT uid
FROM user
WHERE uname = #{uname} and uemail = #{uemail}

SELECT grade, uname
FROM user
WHERE uid = #{uid}

SELECT count(*)
FROM user
WHERE uid = #{uid} and upw = #{upw}

SELECT count(uemail)
FROM user
WHERE uemail=#{uemail}

SELECT count(uid)
FROM user
WHERE uid = #{uid}
